// source: http://prystash.blogspot.ca/2009/06/groovy-base64-encodedecode-with-and.html
import org.apache.commons.codec.binary.Base64

def input = context.expand( '${#TestCase#input_txt}' )
def bytes = input.bytes

//Base64 coder = new Base64()
//def encodedData = coder.encode(bytes)

def encodedData = bytes.encodeBase64().toString()

testCase.setPropertyValue( "input", encodedData )


