con:testCase.con:setupScript:
// source: http://prystash.blogspot.ca/2009/06/groovy-base64-encodedecode-with-and.html
import org.apache.commons.codec.binary.Base64

//testCase.setPropertyValue( "input1", context.expand( '${#TestCase#input1.txt}').bytes.encodeBase64().toString() )
//testCase.setPropertyValue( "input2", context.expand( '${#TestCase#input2.txt}').bytes.encodeBase64().toString() )

// Could also be:
//<srcString xsi:type="xsd:string">${=context.getTestCase().getPropertyValue('delme').replaceAll('<', '&lt;')}</srcString>
for (prop in ["00", "01", "02", "03", "04", "05", "06", "07", "08", "09", "10", "11", "12"]) {
	prop = 'input' + prop
	testCase.setPropertyValue(prop, context.expand('${#TestCase#' + prop + '.txt}').replaceAll('<', "&lt;"))
}

