con:testSuite.con:setupScript:
// source: http://prystash.blogspot.ca/2009/06/groovy-base64-encodedecode-with-and.html
import org.apache.commons.codec.binary.Base64
log.info('Z' + testSuite.getPropertyValue( 'fiches_termino_utf-8.txt').count("\t"))
testSuite.setPropertyValue( "delme.b64", context.expand( '${#TestSuite#fiches_termino_utf-8.txt}').bytes.encodeBase64().toString())
testSuite.setPropertyValue( "delme.txt", new String(context.expand( '${#TestSuite#fiches_termino_utf-8.b64}').decodeBase64(), "UTF-8"))
